from pathlib import Path
import pandas as pd
import re
from langflow.base.data.utils import TEXT_FILE_TYPES
from langflow.custom import Component
from langflow.io import BoolInput, FileInput, Output
from langflow.schema import Data


class FileComponent(Component):
    display_name = "File"
    description = "A CSV file loader optimized for content extraction."
    icon = "file-text"
    name = "File"

    inputs = [
        FileInput(
            name="path",
            display_name="Path",
            file_types=["csv"],
            info="Supported file type: CSV",
        ),
        BoolInput(
            name="silent_errors",
            display_name="Silent Errors",
            advanced=True,
            info="If true, errors will not raise an exception.",
        ),
    ]

    outputs = [
        Output(display_name="Data", name="data", method="load_file"),
    ]

    def load_file(self) -> Data:
        if not self.path:
            raise ValueError("Please, upload a file to use this component.")
        
        resolved_path = self.resolve_path(self.path)
        silent_errors = self.silent_errors

        extension = Path(resolved_path).suffix[1:].lower()
        if extension != "csv":
            raise ValueError(f"Unsupported file type: {extension}")

        try:
            df = pd.read_csv(resolved_path, encoding='ISO-8859-1') 
        except Exception as e:
            if silent_errors:
                return Data(status="Error loading CSV file.")
            else:
                raise e

        relevant_columns = [
            "Transcripts", "Caption", "Hashtags", "Views", 
            "Likes", "Comments", "Shares", "Bookmark", "Duration", "Date Posted"
        ]
        
        if not all(col in df.columns for col in relevant_columns):
            raise ValueError("Required columns are missing from the CSV file.")
        
        extracted_data = df[relevant_columns]

        def clean_text(text):
            if pd.isna(text):
                return ""
            text = re.sub(r'\s+', ' ', text) 
            text = re.sub(r'[^\w\s,.#@]', '', text) 
            return text.strip()

        extracted_data[['Transcripts', 'Caption', 'Hashtags']] = extracted_data[['Transcripts', 'Caption', 'Hashtags']].applymap(clean_text)

        combined_text = extracted_data.apply(lambda row: " ".join(map(str, row)), axis=1).str.cat(sep='\n')

        return Data(text=combined_text)
